import { defineStore } from 'pinia'

export const useSettingsStore = defineStore('settings', {
  state: () => ({
    systemName: '',
    systemDesc: '',
    showWelcome: true  // 添加欢迎语显示状态
  }),
  
  persist: {
    enabled: true,
    strategies: [
      {
        storage: localStorage,
        paths: ['systemName', 'systemDesc', 'showWelcome']  // 添加到持久化列表
      }
    ]
  }
})
