import { useUserStore } from '../stores/user'

export const vPermission = {
  mounted(el, binding) {
    const userStore = useUserStore()
    const { value } = binding
    
    if (value) {
      const hasPermission = userStore.hasPermission(value)
      if (!hasPermission) {
        el.parentNode && el.parentNode.removeChild(el)
      }
    } else {
      throw new Error('需要指定权限值')
    }
  }
}

export const vAnyPermission = {
  mounted(el, binding) {
    const userStore = useUserStore()
    const { value } = binding
    
    if (value && Array.isArray(value)) {
      const hasPermission = userStore.hasAnyPermission(value)
      if (!hasPermission) {
        el.parentNode && el.parentNode.removeChild(el)
      }
    } else {
      throw new Error('需要指定权限值数组')
    }
  }
}

export const vAllPermissions = {
  mounted(el, binding) {
    const userStore = useUserStore()
    const { value } = binding
    
    if (value && Array.isArray(value)) {
      const hasPermission = userStore.hasAllPermissions(value)
      if (!hasPermission) {
        el.parentNode && el.parentNode.removeChild(el)
      }
    } else {
      throw new Error('需要指定权限值数组')
    }
  }
}
