import { createApp } from 'vue'
import { createPinia } from 'pinia'
import ElementPlus from 'element-plus'
import 'element-plus/dist/index.css'
import App from './App.vue'
import router from './router'
import { usePermissionStore } from './stores/permission'

// 添加全局错误处理
const ignoreResizeObserverError = () => {
  if (typeof window !== 'undefined') {
    const errorHandler = (e) => {
      if (e && e.message && e.message.includes('ResizeObserver')) {
        const resizeObserverErr = e instanceof Error && e.message.includes('ResizeObserver loop')
        if (resizeObserverErr) {
          e.stopImmediatePropagation?.()
          return false
        }
      }
    }

    window.addEventListener('error', errorHandler)
    window.addEventListener('unhandledrejection', errorHandler)
  }
}
ignoreResizeObserverError()

const app = createApp(App)
const pinia = createPinia()

// 添加 Vue 全局错误处理
app.config.errorHandler = (err, vm, info) => {
  if (err.message?.includes('ResizeObserver')) {
    return false
  }
  console.error('Vue Error:', err, info)
}

app.use(pinia)
app.use(ElementPlus)
app.use(router)

// 初始化权限store
const permissionStore = usePermissionStore()
permissionStore.init()

app.mount('#app')
